import axios from 'axios';
import React,{useEffect, useState} from 'react'
import Map, { Marker,Popup} from 'react-map-gl'
import 'mapbox-gl/dist/mapbox-gl.css'
import './Map.css'


const points = [
    {id:1, title: "Round", lat:51.506, lon: -0.184},
    {id:2, title: "Cylinder", lat:51.508, lon: -0.174},
    {id:3, title: "Round", lat:51.505, lon: -0.164}
]



function MapView() {
  const [redZone, setRedzone] = useState([])
  const [showPopup, setShowPopup] = useState(false);
  const handleButton= ()=>{
          setShowPopup(true)
  }
  useEffect(() => {
     axios.get("http://localhost:5000/getExtreme").then((response) => {
    
      setRedzone(response.data)
    })
  
    return () => {
      
    }
  }, [])
  


  return (
    <div className='map-container'>
      

  <Map
    
    initialViewState={{
      longitude: -0.169,
      latitude: 51.506,
      zoom: 12,
      bearing:0,
      pitch:0
    }}
    style={{width: 850, height: 500}}
    mapboxAccessToken='pk.eyJ1IjoiYWxiaW4tZGV2ZWxlcGVyIiwiYSI6ImNsNjdlaGl2YzBodTYzY281ZGJsdDkwY3UifQ.AbQj0MOBwXQ9Sx6I1lAm1A'
    mapStyle="mapbox://styles/mapbox/streets-v11"
  > 
 

  {redZone.map (({ lat, lon, id, deviation, cityname}) =>{
      return(
          <Marker key={id} latitude={lat} longitude={lon} anchor="bottom">
             <button className='marker-btn' onClick={handleButton} >
               
             </button>
             <div className='hide'>haia</div>
             
          </Marker>

        )
  })}
    </Map>

    

    </div>
  
    )
}

export default MapView
