import mapboxgl from 'mapbox-gl'
import React from 'react'
import Map, { Marker} from 'react-map-gl'
import 'mapbox-gl/dist/mapbox-gl.css'
import MyMarker from '../Marker/MyMarker'
import './Map.css'


const points = [
    {id:1, title: "Round", lat:51.506, lon: -0.184},
    {id:2, title: "Cylinder", lat:51.508, lon: -0.174},
    {id:3, title: "Round", lat:51.505, lon: -0.164}
]

function MapView() {
  return (
    <div className='map'>
      

  <Map
    
    initialViewState={{
      longitude: -0.169,
      latitude: 51.506,
      zoom: 12,
      bearing:0,
      pitch:0
    }}
    style={{width: 600, height: 400}}
    mapboxAccessToken='pk.eyJ1IjoiYWxiaW4tZGV2ZWxlcGVyIiwiYSI6ImNsNjdlaGl2YzBodTYzY281ZGJsdDkwY3UifQ.AbQj0MOBwXQ9Sx6I1lAm1A'
    mapStyle="mapbox://styles/mapbox/streets-v11"
  > 
  {points.map (({ lat, lon, id, title}) =>{
      return(
          <Marker key={id} latitude={lat} longitude={lon} anchor="bottom">
             <button className='marker-btn'>
               
             </button>
          </Marker>
      )
  })}
  
  </Map>

    </div>
  
    )
}

export default MapView
